#!/usr/bin/env python3

""" Compiles all data generated by atn-generate-convergence-simulations.py.
The results will be stored in DATA_HOME/convergence-data.
"""

from atntools import convergenceprocess

print("""
    The data from the following cvg-sequences will be compiled
    and written to {}, overwriting any existing data.
    Are you sure?
""".format(convergenceprocess.get_cvg_data_dir()))

answer = input()
if len(answer) > 0 and answer[0] == 'y':
    convergenceprocess.compile_data()
